package week1.day2;

public class ConvertANegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = -40;
		int num1 = -1;
		int num2;
		
		
		if(num < 0)
		{
			
			num2 = num*num1;
			System.out.println(num2 + " is a positive number");
		}
		


	}

}
